{
    "assessment_methods": {
        "practical_performance": {
            "name": "Practical Performance (PP)",
            "description": "A practical Performance (PP) assessment will provide direct evidence of whether candidates have acquired the competency for the ability statements by solving a scenario-based problem.",
            "focus": "The Practical Performance (PP) assessment focuses on providing authentic 'Show Me Application' evidence of candidates' ability to implement Azure cloud solutions effectively.",
            "tasks": [
                "Candidates will complete practical tasks that demonstrate their ability to configure Azure services and resolve implementation issues."
            ],
            "evidence": {
                "LO1": "Candidates will assess cloud solutions by analyzing performance metrics and drafting specifications for Azure cloud computing.",
                "LO2": "Candidates will develop a comprehensive implementation plan for deploying Azure services, including virtual machines and networking.",
                "LO3": "Candidates will create review processes to monitor and resolve issues related to Azure cloud implementations."
            },
            "submission": [
                "Candidates will submit their implementation plans, assessment reports, and review process documentation electronically via the learning management system.",
                "Submissions will include detailed descriptions of the tasks performed and the outcomes achieved."
            ],
            "marking_process": [
                "Effectiveness of Implementation Plan.",
                "Quality of Assessment Reports.",
                "Clarity of Review Processes."
            ],
            "retention_period": "All submitted evidence, including implementation plans and assessment reports, will be retained for 3 years for compliance and auditing purposes."
        }
    }
}

