message_thread.content:
{
    "assessment_methods": {
        "practical_performance": {
            "name": "Practical Performance (PP)",
            "description": "A practical Performance (PP) assessment will provide direct evidence of whether candidates have acquired the competency for the ability statements by solving a scenario-based problem.",
            "focus": "The Practical Performance (PP) assessment focuses on providing authentic 'Show Me Application' evidence of candidates' ability to create and critique digital graphics using Canva.",
            "tasks": [
                "Candidates will complete practical tasks that demonstrate their ability to blend typography with media elements in Canva."
            ],
            "evidence": {
                "LO1": "Candidates will develop a visual communication project in Canva, integrating typography and media elements effectively.",
                "LO2": "Candidates will create a storyboard in Canva to communicate visual intentions and evaluate design enhancements.",
                "LO3": "Candidates will collaborate in groups to critique and improve Canva designs based on best practices."
            },
            "submission": [
                "Candidates will submit their completed Canva projects and storyboards along with a reflective document detailing their design choices and enhancements."
            ],
            "marking_process": [
                "Creativity and Originality.",
                "Technical Execution.",
                "Collaboration and Feedback Integration."
            ],
            "retention_period": "All submitted evidence, including Canva projects and reflective documents, will be retained for 3 years for compliance and auditing purposes."
        }
    }
}

