{
    "assessment_methods": {
        "oral_questioning": {
            "name": "Oral Questioning (OQ)",
            "description": "Oral Questioning assessments allow candidates to demonstrate their understanding of concepts through verbal responses, focusing on the practical application of hydroponics and urban farming.",
            "evidence": {
                "LO1": "Candidates will explain standard operating procedures for farming activities and equipment operations.",
                "LO2": "Candidates will discuss regulations, WSH practices, and housekeeping rules relevant to farming operations."
            },
            "submission": [
                "Candidates will verbally respond to assessors during a structured questioning session."
            ],
            "marking_process": [
                "Clarity of Responses.",
                "Depth of Knowledge.",
                "Relevance to Learning Outcomes."
            ],
            "retention_period": "All oral questioning recordings and assessment notes will be retained for 2 years for compliance and auditing."
        }
    }
}

