{
  "assessment_methods": {
    "practical_performance": {
      "name": "Practical Performance (PP)",
      "description": "A practical Performance (PP) assessment will provide direct evidence of whether candidates have acquired the competency for the ability statements by solving a scenario-based problem.",
      "focus": "The Practical Performance (PP) assessment focuses on providing authentic 'Show Me Application' evidence of candidates' ability to apply Kubernetes concepts in real-world scenarios.",
      "tasks": [
        "Candidates will complete practical tasks that demonstrate their ability to design, deploy, and manage Kubernetes applications effectively."
      ],
      "evidence": {
        "LO1": "Candidates will create a Kubernetes solution architecture that incorporates various processes and tools.",
        "LO2": "Candidates will align technical and functional requirements within a Kubernetes deployment scenario.",
        "LO3": "Candidates will coordinate multiple Kubernetes components to ensure compatibility and meet design goals.",
        "LO4": "Candidates will articulate the value of their Kubernetes solutions by addressing coding standards and scalability.",
        "LO5": "Candidates will establish monitoring and testing processes to validate their Kubernetes architectures."
      },
      "submission": [
        "Candidates will submit their completed Kubernetes configurations, deployment scripts, and any additional documentation that supports their practical tasks.",
        "This includes annotated screenshots or logs demonstrating the functionality of their Kubernetes applications."
      ],
      "marking_process": [
        "Functionality of Kubernetes solutions.",
        "Adherence to best practices.",
        "Quality of documentation and submission."
      ],
      "retention_period": "All submitted evidence, including Kubernetes configurations and assessment records, will be retained for 3 years for compliance and auditing purposes."
    }
  }
}

