}

{
    "Assessment Methods": {
        "Assessment Methods": [
            "Written Assessment",
            "Oral Questioning"
        ],
        "Amount of Practice Hours": "2",
        "Course Outline": {
            "Learning Units": {
                "LU1": {
                    "Description": [
                        {
                            "Topic": "Topic 1: Introduction to Hydroponics and Systems (A1, K1)",
                            "Details": [
                                "Definition and history of hydroponics",
                                "Advantages and disadvantages of hydroponics",
                                "Basic components of a hydroponic system",
                                "Different types of hydroponic systems (e.g., NFT, DWC, Ebb and Flow)",
